window.Dropdown = {
  init () {
    this.bind()
  },
  bind () {
    const dropdowns = document.querySelectorAll('.nav-dropdown')

    dropdowns.forEach(function (element) {
      this.initElement(element)
    }, this)
  },
  unbind(){
    const dropdowns = document.querySelectorAll('.nav-dropdown')

    dropdowns.forEach(function (element) {
      this.deInitElement(element)
    }, this)
  },
  initElement(element){
    element.addEventListener('mouseover', this.listeners.showDropdown)
    element.addEventListener('mouseout', this.listeners.hideDropdown)
  },
  deInitElement(element){
    element.removeEventListener('mouseover', this.listeners.showDropdown)
    element.removeEventListener('mouseout', this.listeners.hideDropdown)
  },
  listeners: {
    showDropdown () {
      this.querySelector('.dropdown').style.display = 'block'
    },
    hideDropdown () {
      this.querySelector('.dropdown').style.display = 'none'
    }
  }
}

Dropdown.init()
